import React from 'react';
import { formatPrice } from '../helpers';
import PropTypes from 'prop-types';

class Fish extends React.Component {
  static propTypes = {
    details: this.propTypes.shape({
      image: PropTypes.string,
      name: PropTypes.string,
      desc: PropTypes.string,
      status: PropTypes.string,
      price: PropTypes.number
    }),
    addToOrder: PropTypes.func
  }
  handleClick = () => {
    this.props.addToOrder(this.props.index);
  }
  render() {
    const { name, price, status, desc, image } = this.props.details;
    const isAvailable = status === 'available';
    
    return (
      <li className="menu-fish">
        <img src={image} alt=""/>
        <h3 className="fish-name">{name}
        <span className="price">{formatPrice(price)}</span></h3>
        <p>{desc}</p>
    <button disabled={!isAvailable} onClick={this.handleClick}>{isAvailable ? 'Add to Cart' : 'Sold Out'}</button>
      </li>
    )
  }
}

export default Fish;
